/**
 * @see http://github.com/NV/placeholder.js
 */jQuery.fn.textPlaceholder=function(){return this.each(function(){var e=this;if(e.placeholder&&"placeholder"in document.createElement(e.tagName))return;var t=e.getAttribute("placeholder"),n=jQuery(e);if(e.value===""||e.value==t){n.addClass("text-placeholder");e.value=t}n.focus(function(){if(n.hasClass("text-placeholder")){this.value="";n.removeClass("text-placeholder")}});n.blur(function(){if(this.value===""){n.addClass("text-placeholder");this.value=t}else n.removeClass("text-placeholder")});e.form&&jQuery(e.form).submit(function(){n.hasClass("text-placeholder")&&(e.value="")})})};